// import ButtonAppBar from "@components/Header";

import React, { useEffect, useState, useRef } from "react";

import { Box, Grid, Stack, Typography } from "@mui/material";
import { DefaultCard } from "components/Card";
import LoadingScreen from "components/LoadingScreen";
import { delay } from "utils/helper";
import { FadeInWhenVisible } from "components/Animations";
import {
  animate,
  motion,
  useMotionTemplate,
  useMotionValue,
  useSpring,
  useTransform,
} from "framer-motion";
import styled from "@emotion/styled";
import BACKGROUND_IMG from "assets/doodle-background.png";

// ...

const RotationWrapper = styled(motion.div)`
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  transform-style: preserve-3d;
`;

export default function AccountCard() {
  // handle mouse move on document

  return (
    <FadeInWhenVisible>
      <DefaultCard
        style={{
          backgroundImage: sheenGradient,
          boxShadow: boxShadow,
        }}
      >
        <Box
          sx={{
            backgroundImage: `url(${BACKGROUND_IMG})`,
            backgroundSize: "cover",
          }}
        >
          <Grid
            container
            sx={{
              aspectRatio: "2/1",
            }}
          >
            <Grid item xs={5} md={4} sx={{ padding: 2 }}>
              <motion.div
                style={{
                  boxShadow: boxShadow,
                  borderRadius: 8,
                  backgroundColor: "white",
                }}
              >
                <img
                  style={{
                    width: "100%",
                  }}
                  src="./OPS.png"
                />
              </motion.div>

              <Typography
                variant={"h5"}
                fontWeight={700}
                fontSize={10}
                sx={{
                  textTransform: "uppercase",
                  letterSpacing: 3,
                  color: "#294283",
                  m: 0,
                }}
              >
                Active Since
              </Typography>
              <Typography
                sx={{
                  textTransform: "uppercase",
                  color: "#294283",
                }}
              >
                2023
              </Typography>
            </Grid>
            <Grid item xs={7} md={8}>
              <Stack
                sx={{
                  flex: 2,
                  height: "100%",
                  padding: 2,
                }}
              >
                <Typography
                  variant={"h5"}
                  fontWeight={700}
                  fontSize={10}
                  sx={{
                    textTransform: "uppercase",
                    letterSpacing: 3,
                    color: "#294283",
                    m: 0,
                  }}
                >
                  Portfolio
                </Typography>
                <Typography
                  sx={{
                    textTransform: "uppercase",
                    color: "#294283",
                  }}
                >
                  Operations
                </Typography>
                <Typography
                  variant="h5"
                  fontWeight={700}
                  fontSize={10}
                  sx={{
                    textTransform: "uppercase",
                    letterSpacing: 3,
                    color: "#294283",
                  }}
                >
                  Name
                </Typography>
                <Typography
                  sx={{
                    textTransform: "uppercase",
                    color: "#294283",
                  }}
                >
                  Ricky Winarko
                </Typography>
                <Typography
                  variant="h5"
                  fontWeight={700}
                  fontSize={10}
                  sx={{
                    textTransform: "uppercase",
                    letterSpacing: 3,
                    color: "#294283",
                  }}
                >
                  Membership
                </Typography>
                <Typography
                  sx={{
                    textTransform: "uppercase",
                    color: "#294283",
                  }}
                >
                  SubCom
                </Typography>
              </Stack>
            </Grid>
          </Grid>
          <Grid item xs={12}>
            <Typography
              fontSize={18}
              fontWeight={600}
              sx={{
                textAlign: "right",
                textTransform: "uppercase",
                letterSpacing: 3,
                color: "white",
              }}
            >
              Garage@EEE
            </Typography>
          </Grid>
        </Box>
      </DefaultCard>
    </FadeInWhenVisible>
  );
}
