import * as React from "react";
import { motion } from "framer-motion";
import { alpha, styled } from "@mui/material/styles";
import { Grid, Typography, Stack, Card } from "@mui/material";
import ClanMetadata from "../config/prop-config.json";
import Image from "next/image";
import { RankMapping } from "../config/constants";

const CustomCard = styled(motion("div"))(({ theme }) => ({
  background: `${alpha(theme.palette.secondary.main, 1)}`,
  width: "100%",
  minHeight: "1vh",
  // maxHeight: "48vh",
  alignItems: "center",
  // backdropFilter: "blur(15px)",
  padding: 4,
  borderRadius: 15,
  boxShadow: "0px 5px 6px 5px rgba(0,0,0,0.2)",
}));

export function DefaultCard({ children, sx, innerRef, style }) {
  return (
    <CustomCard sx={sx} ref={innerRef} style={style}>
      {children}
    </CustomCard>
  );
}

export function ClanCard({ children, clan, rank, onClick, ...sx }) {
  const variants = {
    initial: { opacity: 0, scale: 0 },
    animate: { opacity: 1, scale: 1 },
  };

  return (
    <CustomCard
      initial="initial"
      animate="animate"
      variants={variants}
      whileHover={{ scale: 1.03 }}
      whileTap={{ scale: 0.9 }}
      transition={{ type: "spring", stiffness: 400, damping: 30 }}
      onClick={onClick}
      clan={clan ? clan : "invicta"}
      sx={{
        ...sx,
        background: `rgba(27,27,27,0.8)`,
        padding: "3%",
        alignItems: "center",
      }}
    >
      <Stack alignItems="center" sx={{ width: "100%" }}>
        <Typography fontWeight={700} sx={{ width: "100%" }}>
          {RankMapping[rank]}
        </Typography>
        <Stack alignItems="center" sx={{ paddingX: "10%" }}>
          <Image
            width={220}
            height={220}
            src={ClanMetadata[clan]?.img}
            alt={clan}
          />
        </Stack>
        <Typography
          variant={"h5"}
          fontWeight={700}
          sx={{
            textAlign: "center",
            textTransform: "uppercase",
            letterSpacing: 3,
          }}
        >
          {clan}
        </Typography>
        <Typography
          sx={{
            textAlign: "center",
          }}
        >
          {ClanMetadata[clan]?.desc}
        </Typography>
        {children}
      </Stack>
    </CustomCard>
  );
}
