import React from "react";
import { API, HEADER_HEIGHT } from "../../utils/constants";
import { useState, useEffect } from "react";
import AppHeader from "../../components/AppHeader/AppHeader";
import {
  Button,
  Box,
  LinearProgress,
  Grid,
  Typography,
  TextField,
  InputAdornment,
  IconButton,
  Stack,
  Pagination,
} from "@mui/material";
import EventCard from "components/EventCard/EventCard";
import { Search, FilterList } from "@mui/icons-material";
import { getRoute } from "utils/routes";
import { useNavigate } from "react-router-dom";
import { convertToHierarchy } from "utils/DirectoryListing";
import XMLParser from "react-xml-parser";
import { useMetadataContext } from "context/MetadataContext";
import Noise from "assets/noise.svg";
import moment from "moment";
import { motion } from "framer-motion";
import ScaleUpOnHover from "utils/Animations/ScaleUpOnHover";
import MultipleSelectChip from "components/Reusables/MultiSelect";
import BACKGROUND_IMG from "assets/EOS_PRODUCT.png";
import EOS_RS_LOGO from "assets/EOS-RS-Logo.png";
import KeyboardDoubleArrowDownIcon from "@mui/icons-material/KeyboardDoubleArrowDown";
import AccountCard from "components/MainPage/AccountCard";
import LoadingScreen from "components/LoadingScreen";

const itemsPerPage = 8;

const HomePage = () => {
  const metadata = useMetadataContext();
  const Navigate = useNavigate();
  const [query, setQuery] = useState("");
  const [startDate, setStartDate] = useState(moment().subtract(6, "months"));
  const [endDate, setEndDate] = useState(moment());
  const [filter, setFilter] = useState([]);

  const [page, setPage] = useState(1);

  const handlePageChange = (event, value) => {
    setPage(value);
  };

  const filteredMetadata = metadata?.filter((item) => {
    return (
      item.product_list[0].prod_desc
        .toLowerCase()
        .includes(query.toLowerCase()) &&
      (filter.length == 0 ||
        item.event_type_tags.some((r) => {
          return filter.map((i) => i.toLowerCase()).includes(r);
        }))
    );
  });

  const startIndex = (page - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const paginatedData = filteredMetadata?.slice(startIndex, endIndex);

  useEffect(() => {
    setPage(1);
  }, [filteredMetadata?.length]);

  const handleChange = (event) => {
    setQuery(event.target.value);
  };

  const mostRecentMetadata = metadata?.filter((item) => {
    const itemDate = moment(item.event_start);
    return itemDate.isAfter(startDate) && itemDate.isBefore(endDate);
  });

  const MapEventCards = (inputData) => {
    return inputData?.map((item) => {
      var latestProduct = item.product_list.find((data) => {
        return data.isLatest == true;
      });

      // console.log(item);
      return (
        <EventCard
          Title={item.event_display_name}
          Image={latestProduct.prod_main_png}
          Description={latestProduct.prod_desc}
          Date={`${item.event_start} | ${item.event_end}`}
          LastUpdated={latestProduct.prod_date}
          Tags={item.event_type_tags}
          key={item.event_display_name}
          onClick={() => {
            Navigate(getRoute("leaflet"), {
              state: {
                event: item,
                product_list: item.product_list,
              },
            });
          }}
        />
      );
    });
  };

  return (
    <>
      {true ? (
        <LoadingScreen />
      ) : (
        <Box>
          <AppHeader />

          <Box
            sx={{
              backgroundImage: `linear-gradient(0deg, rgba(0, 0, 0, 0.1), rgba(0, 0, 0, 0.05)), url(${BACKGROUND_IMG})`,
              backgroundSize: "cover",
              backgroundPosition: "center",
              display: "flex",
              width: "100%",
              height: "100vh",
            }}
          >
            <Box
              sx={{
                background: "#00000088",
                flex: 1,
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                flexDirection: "column",
                gap: "1rem",
              }}
            >
              <img src={EOS_RS_LOGO} height={"80px"} />
              <Typography
                variant="h5"
                textAlign="center"
                width="60%"
                // fontWeight="800"
                color="white"
              >
                The EOS Remote Sensing Lab (EOS-RS) is a flagship laboratory at
                the Earth Observatory of Singapore. We use satellite
                remote-sensing data for rapid disaster response and hazard
                monitoring.
              </Typography>
              <Button
                variant="outlined"
                // type="primary"

                color="primary"
                sx={{ width: "50%", borderRadius: "5rem", marginTop: "2rem" }}
                onClick={(e) => {
                  let events = document
                    .getElementById("events")
                    .getBoundingClientRect();
                  e.preventDefault(); // Stop Page Reloading
                  events &&
                    window.scrollTo({
                      behavior: "smooth",
                      top:
                        events.top -
                        document.body.getBoundingClientRect().top -
                        parseInt(HEADER_HEIGHT, 10),
                    });
                }}
                // onClick={() => {
                //   Navigate(getRoute("home"));
                // }}
                // href="https://earthobservatory.sg/research/centres-labs/eos-rs"
              >
                View products
              </Button>
              <IconButton size="large">
                <KeyboardDoubleArrowDownIcon />
              </IconButton>
            </Box>
          </Box>
          <Box
            sx={{
              display: "flex",
              flexDirection: " column",
              flexGrow: 1,
              padding: "2rem",
              gap: "3rem",
              paddingTop: HEADER_HEIGHT,
            }}
          >
            {/* <Box>
          <Typography variant="h3" fontWeight="800">
            Recent events
          </Typography>
          <Typography variant="h6">Events in the last 6 months</Typography>
          <Grid
            sx={{ padding: "1rem" }}
            container
            spacing={{ xs: 2, md: 3 }}
            columns={{ xs: 2, sm: 8, md: 12 }}
          >
            {MapEventCards(mostRecentMetadata)}
          </Grid>
        </Box> */}

            <Box sx={{ minHeight: "80vh" }}>
              <Box
                sx={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                }}
              >
                <Box
                  sx={{ display: "flex", flexDirection: "column" }}
                  id={"events"}
                >
                  <Typography variant="h3" fontWeight="800">
                    All events
                  </Typography>
                  <Typography variant="h6">Click to view</Typography>
                </Box>

                <Stack direction={"row"} alignItems={"center"}>
                  <MultipleSelectChip
                    itemName={filter}
                    setItemName={setFilter}
                  />

                  <TextField
                    label="Search"
                    variant="outlined"
                    // size="small"
                    onChange={handleChange}
                    InputProps={{
                      endAdornment: (
                        <InputAdornment position="end">
                          <IconButton>
                            <Search />
                          </IconButton>
                        </InputAdornment>
                      ),
                      // endAdornment: (
                      //   <InputAdornment position="end">
                      //     <IconButton>
                      //       <FilterList />
                      //     </IconButton>
                      //   </InputAdornment>
                      // ),
                      style: { borderRadius: "20px" },
                    }}
                  />
                </Stack>
              </Box>
              <AccountCard />
              <Grid
                sx={{ padding: "1rem" }}
                container
                spacing={{ xs: 2, md: 3 }}
              >
                {MapEventCards(paginatedData)}
              </Grid>
              <Box width={"100%"} display={"flex"} justifyContent={"center"}>
                <Pagination
                  // variant="outlined"
                  color="primary"
                  count={Math.ceil(filteredMetadata?.length / itemsPerPage)}
                  page={page}
                  onChange={handlePageChange}
                  size="large"
                />
              </Box>
            </Box>
          </Box>
        </Box>
      )}
    </>
  );
};

export default HomePage;
